package main

import (
	"testing"

	"github.com/stretchr/testify/require"
)

func Test_PartOne(t *testing.T) {

	require.Equal(t, 7, PartOne("mjqjpqmgbljsphdztnvjfqwrcgsmlb"))

	require.Equal(t, 5, PartOne("bvwbjplbgvbhsrlpgdmjqwftvncz"))
	require.Equal(t, 6, PartOne("nppdvjthqldpwncqszvftbrmjlhg"))
	require.Equal(t, 10, PartOne("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"))
	require.Equal(t, 11, PartOne("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"))

	require.Equal(t, 1210, PartOne(""))
}

func PartOne(s string) int {

	for i := 0; i <= len(s)-4; i++ {

		// the quickest O(N), no overthinking
		if s[i] == s[i+1] || s[i] == s[i+2] || s[i] == s[i+3] ||
			s[i+1] == s[i+2] || s[i+1] == s[i+3] ||
			s[i+2] == s[i+3] {
			continue
		}
		return i + 4
	}

	return 0
}

func Test_PartTwo(t *testing.T) {

	// part two with frame size == 4 should be equal to part one
	require.Equal(t, PartOne("mjqjpqmgbljsphdztnvjfqwrcgsmlb"), PartTwo(4, "mjqjpqmgbljsphdztnvjfqwrcgsmlb"))

	require.Equal(t, 19, PartTwo(14, "mjqjpqmgbljsphdztnvjfqwrcgsmlb"))
	require.Equal(t, 23, PartTwo(14, "bvwbjplbgvbhsrlpgdmjqwftvncz"))
	require.Equal(t, 23, PartTwo(14, "nppdvjthqldpwncqszvftbrmjlhg"))
	require.Equal(t, 29, PartTwo(14, "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"))
	require.Equal(t, 26, PartTwo(14, "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"))
	require.Equal(t, 3476, PartTwo(14, ""))
}

func PartTwo(frame int, s string) int {

	for i := 0; i <= len(s)-frame; i++ {

		// like O(N*frame) in the worst case
		memo := make(map[byte]bool)
		for j := i; j <= i+frame-1; j++ {
			if _, ok := memo[s[j]]; ok {
				break
			}

			memo[s[j]] = true
			if j == i+frame-1 {
				return j + 1
			}
		}
	}

	return 0
}
